mapper.select:
select 

from t_users
where 1=1

   and uid = #{uid}


   and username = #{username}


   and password = #{password}


   and email = #{email}


   and home_url = #{homeUrl}


   and screen_name = #{screenName}


   and created = #{created}


   and activated = #{activated}


   and logged = #{logged}


   and group_name = #{groupName}
